INSERT INTO `vers` VALUES ('25', '58-1', '2012.11.29 10：30', '李燊', '更新温泉系统的退出场景\r\n更新商路系统的消费数据、增加商路系统的军团技能加成\r\n更新异民族系统的退出场景');
INSERT INTO `vers` VALUES ('26', '58-1', '2012.11.29 10:30', '何智荣', '1.修改登录流程，加快登录速度；\r\n2.修复多人组队的bug;\r\n3.修改任务的bug;\r\n4.人物属性整理，属性加成流程整理\r\n5.地图流程整理；出入点随机化处理');
INSERT INTO `vers` VALUES ('27', '58', '2012/11/30 11:53', '张鑫', '更新任务表 技能表');
INSERT INTO `vers` VALUES ('28', '58-2', '2012.12.4 21:30', '何智荣', '1.修复官衔任务等各种支线任务\r\n2.地图进入加了限制');
INSERT INTO `vers` VALUES ('29', '58-2', '2012.12.4 21:30', '黄源甲', '1、招募系统修改\r\n2、人物武将初始上阵修改\r\n3、修改武将装备心法属性加成bug');
INSERT INTO `vers` VALUES ('30', '58-2', '2012.12.4 21:30', '赵鸿', '1.部分被动技能初版\r\n2.战斗中下线再上线BUG\r\n3.个别主动技能BUG（如移除BUFF效果）');
INSERT INTO `vers` VALUES ('31', '58-2', '2012.12.4 21:30', '刘博', '1.常规组队去除国家和军团限制，对职业进行限制\r\n2.增加了闯塔的排行榜');
INSERT INTO `vers` VALUES ('32', '58-2', '2012.12.4 21:30', '胡威', '1心法参阅消耗修改\r\n2心法拾取BUG修改');
INSERT INTO `vers` VALUES ('33', '58-2', '2012.12.4 21:30', '钟键', '1.修复排行榜发放物品的bug。2.修复军团职位提升bug。');
INSERT INTO `vers` VALUES ('34', '58-2', '2012.12.4 21:30', '李燊', '1、修复商路系统建造市场的提示和面板上军团木牛流马技能的加成。\r\n2、修复福利系统离线礼包领取经验的问题。\r\n3、更新异民族系统的基础数据表。');
INSERT INTO `vers` VALUES ('35', '58-3', '2012.12.6 20:00', '黄源甲', '1、修改人物升级属性bug,2、修改军团术法属性加成buf,3、妖魔破浴火重生bug、伤害统计bug');
INSERT INTO `vers` VALUES ('36', '58-3', '2012.12.6 20:00', '胡威', '1竞技场数据持久化方式修改\r\n2祈天转化 拾取新需求功能添加\r\n3强化重复发送TIPS更正\r\n4GM模块结构更改\r\n\r\n');
INSERT INTO `vers` VALUES ('37', '58-3', '2012.12.6 20:00', '钟键', '1.修复坐骑生成基础属性错误bug。2.修复军团宴会有色子分数错误bug.3.修复排行榜显示不可出战武将bug。');
INSERT INTO `vers` VALUES ('38', '58-3', '2012.12.6 20:00', '何智荣', '1.增加活动任务\r\n2.调整人物离线数据的持久化\r\n3.与前端联调扫荡的调整功能点、任务的调整功能点\r\n4.修复多人组队调整阵型的bug\r\n5.调整常规组队的加成规则与数值');
INSERT INTO `vers` VALUES ('39', '58-3', '2012.12.6 20:00', '赵鸿', '1.修改部分天赋技能\r\n2.修改部分主动技能BUG\r\n3.修改自动战斗选技能需求\r\n4.修改战斗广播\r\n5.添加主动技能使用CD限制');
INSERT INTO `vers` VALUES ('41', '58-3', '2012.12.6 20:00', '李燊', '1、更新课程表系统的活跃度。\r\n2、更新组队系统，队伍开始后不能加入。\r\n3、增加异民族系统的奖励发放。');
INSERT INTO `vers` VALUES ('42', '58-3', '2012.12.6 20:00', '何勇', '更新内容：\r\n1.替换心法ui，增加锁定心法，临时背包心法转化等功能\r\n2.修改小地图按钮，npc名称显示\r\n3.修改玩家在战斗中键盘导致人物的移动\r\n4.增加副本go提示');
INSERT INTO `vers` VALUES ('40', '58-3', '2012.12.6 20:00', '刘博', '1.修改系统邮件,单独算数量,不计入总邮件数里\r\n2.修改常规组队队长离开队伍的bug\r\n3.修改闯塔打完50关以后不需要重置就能继续从41关打的bug');
INSERT INTO `vers` VALUES ('43', '58-3', '2012.12.6 20:00', '宋超', '1新版道具点UI\r\n2闲聊消息添加任务触发，剧情触发，进入场景触发，登陆触发\r\n3修复获得武将消息提示武将名称颜色\r\n4修复鸿雁飞书BUG');
INSERT INTO `vers` VALUES ('44', '58-3', '2012.12.6 20:00', '刘文清', '1.战斗表现相关。\r\n2.被动技能。\r\n3.战斗结算修改。\r\n4.技能UI部分修改。');
INSERT INTO `vers` VALUES ('45', '58-3', '2012.12.6 20:00', '姚铸', '1. 修改培养属性自动取消及2次确认功能。2. 修正装备tips卡死bug。3. 修改npc对话框。 修正接受任务时点击npc对话框与点击任务条目link处理一统一的bug。4. 修改剧情对话框。 增加剧情红屏处理，黑屏+动画效果，修正剧情伙伴待机动作无响应bug');
INSERT INTO `vers` VALUES ('46', '58-4', '2012.12.17 20:00', '赵鸿', '战斗、主动技能、天赋技能');
INSERT INTO `vers` VALUES ('47', '58-4', '2012.12.18 20:00', '何智荣', '修复了副本的战斗处理;');
INSERT INTO `vers` VALUES ('48', '58-5', '2012.12.28 20:31', '何智荣', '1.更新了好友的各种bug以及最近联系人与其他关系之间的关系\r\n2.更新了祝福的处理\r\n3.更新了精英副本的部分处理，等前端联调');
INSERT INTO `vers` VALUES ('49', '58-5', '2012.12.28', '祝声尉', '更新多人组队 多人竞技场，多人异民族，多人副本UI。修改好友界面BUG。更新普通副本，精英副本UI。\r\n更新好友祝福UI。更新军团UI图标。');
INSERT INTO `vers` VALUES ('50', '58-5', '2012.12.28 20:30', '李燊', '1、更新课程表系统，连续签到礼包按周领取，删除找回功能。\r\n2、更新异民族系统，完善活动结束/活动胜利/活动失败时，玩家在战斗中时退出场景的处理，增加异民族系统评价和翻牌的处理。\r\n3、更新怪物攻城系统，完善活动结束/活动胜利/活动失败时，玩家在战斗中时退出场景的处理。');
INSERT INTO `vers` VALUES ('51', '58-5', '2012.12.28 20:20', '姚铸', '1. 新手引导培养不保存下线再上线的遮挡bug, 副本选择图箭头遮挡bug\r\n2. 装备tips, 道具tips改版\r\n3. 人物界面背包装备战力增加标识，装备<br/>tips对比\r\n4. 培养取消bug\r\n5. 阵法布阵改版');
INSERT INTO `vers` VALUES ('52', '58-5', '2012.12.28 20:30', '钟键', '奇门一键升阶，多人竞技场');
INSERT INTO `vers` VALUES ('53', '58-5', '2012.12.28', '王志雄', '1、世界地图伸缩窗口会出现没有完全挡住场景bug 2、修炼进入竞技无中断 副本中修炼无提示 邀请未开启无提示bug3、Npc对话框任务追踪点击下划线后第2次点击无效bug');
INSERT INTO `vers` VALUES ('54', '58-5', '2012-12-28 20:55:40', '何勇', '1.修复副本扫荡关闭错误，体力显示\r\n2.替换每日军情ui，修改部分功能\r\n3.鼠标和键盘操作人物移动的bug\r\n4.新增多人竞技场');
INSERT INTO `vers` VALUES ('55', '58-5', '2012.12.28 20:31', '胡威', '1 平台创建角色、登录角色，任务、商城、货币变动流水日志\r\n2 心法、作坊模块VIP元宝消耗修改\r\n3 后台接口增加IP白名单，校验码，充值接口');
