INSERT INTO `vers` VALUES ('57', '58-7', '2013.01.04 20:30', '李燊', '1、修改课程表系统，测试日常指引和日常活动完成次数。\r\n2、优化多人组队系统和异民族系统的流程和协议，离开副本时返回异民族大厅。');
